        mWriter.write("/>\n");
    } else {
        if (!mJustWroteText) {
            for (int tabIndex = 0; tabIndex < mStack.size() + mIndentingOffset; tabIndex++) mWriter.write(INDENT_STRING);
        }
        mWriter.write("</");
        mWriter.write(name);
