Map<String, Long> getTagCountsByTagName(Set<Long> eventIDsWithTags){
    HashMap<String, Long> counts = new HashMap();
    DBLock.lock();
    try (Statement createStatement = con.createStatement();
        ResultSet rs = // NON-NLS
        createStatement.executeQuery("SELECT tag_name_display_name, COUNT(DISTINCT tag_id) AS count FROM tags" + " WHERE event_id IN (" + StringUtils.join(eventIDsWithTags, ", ") + ")" + " GROUP BY tag_name_id" + " ORDER BY tag_name_display_name")) {
        while (rs.next()) {
            counts.put(rs.getString("tag_name_display_name"), rs.getLong("count"));
        }
    } catch (SQLException ex) {
        LOGGER.log(Level.SEVERE, "Failed to get tag counts by tag name.", ex);
    } finally {
        DBLock.unlock();
    }
    return counts;
}

