List<CorrelationAttributeInstance> getArtifactInstancesByPath(CorrelationAttributeInstance.Type aType,String filePath){
    if (aType == null) {
        throw new EamDbException("Correlation type is null");
    }
    if (filePath == null) {
        throw new EamDbException("Correlation value is null");
    }
    Connection conn = connect();
    List<CorrelationAttributeInstance> artifactInstances = new ArrayList();
    CorrelationAttributeInstance artifactInstance;
    PreparedStatement preparedStatement = null;
    ResultSet resultSet = null;
    String tableName = EamDbUtil.correlationTypeToInstanceTableName(aType);
    String sql = "SELECT " + tableName + ".id, " + tableName + ".value," + tableName + ".file_obj_id," + " cases.case_name, cases.case_uid, data_sources.id AS data_source_id, data_sources.name, device_id, file_path, known_status, comment, data_sources.case_id, data_sources.datasource_obj_id FROM " + tableName + " LEFT JOIN cases ON " + tableName + ".case_id=cases.id" + " LEFT JOIN data_sources ON " + tableName + ".data_source_id=data_sources.id" + " WHERE file_path=?";
    try {
        preparedStatement = conn.prepareStatement(sql);
        preparedStatement.setString(1, filePath.toLowerCase());
        resultSet = preparedStatement.executeQuery();
        while (resultSet.next()) {
            try {
                artifactInstance = getEamArtifactInstanceFromResultSet(resultSet, aType);
                artifactInstances.add(artifactInstance);
            } catch (CorrelationAttributeNormalizationException ex) {
                logger.log(Level.INFO, "Unable to get artifact instance from resultset.", ex);
            }
        }
    } catch (SQLException ex) {
        throw new EamDbException("Error getting artifact instances by artifactType and artifactValue.", ex);
    } finally {
        EamDbUtil.closeStatement(preparedStatement);
        EamDbUtil.closeResultSet(resultSet);
        EamDbUtil.closeConnection(conn);
    }
    return artifactInstances;
}

