List<Long> getEventIDs(Interval timeRange,RootFilter filter){
    Long startTime = timeRange.getStartMillis() / 1000;
    Long endTime = timeRange.getEndMillis() / 1000;
    if (Objects.equals(startTime, endTime)) {
        endTime++;
    }
    ArrayList<Long> resultIDs = new ArrayList();
    DBLock.lock();
    final String query = "SELECT events.event_id AS event_id FROM events" + useHashHitTablesHelper(filter) + useTagTablesHelper(filter) + " WHERE time >=  " + startTime + " AND time <" + endTime + " AND " + SQLHelper.getSQLWhere(filter) + " ORDER BY time ASC";
    try (Statement stmt = con.createStatement();
        ResultSet rs = stmt.executeQuery(query)) {
        while (rs.next()) {
            resultIDs.add(rs.getLong("event_id"));
        }
    } catch (SQLException sqlEx) {
        LOGGER.log(Level.SEVERE, "failed to execute query for event ids in range", sqlEx);
    } finally {
        DBLock.unlock();
    }
    return resultIDs;
}

