boolean isSupportedAbstractFileType(AbstractFile file){
    if (file == null) {
        return false;
    }
    switch(file.getType()) {
        case UNALLOC_BLOCKS:
        case UNUSED_BLOCKS:
        case SLACK:
        case VIRTUAL_DIR:
        case LOCAL_DIR:
            return false;
        case CARVED:
        case DERIVED:
        case LOCAL:
            return true;
        case FS:
            return file.isMetaFlagSet(TskData.TSK_FS_META_FLAG_ENUM.ALLOC);
        default:
            logger.log(Level.WARNING, "Unexpected file type {0}", file.getType().getName());
            return false;
    }
}

