void createInterestingFileHit(AbstractFile file,FileType fileType){
    try {
        Collection<BlackboardAttribute> attributes = new ArrayList();
        attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_SET_NAME, FileTypeIdModuleFactory.getModuleName(), fileType.getInterestingFilesSetName()));
        attributes.add(new BlackboardAttribute(BlackboardAttribute.ATTRIBUTE_TYPE.TSK_CATEGORY, FileTypeIdModuleFactory.getModuleName(), fileType.getMimeType()));
        Case currentCase = Case.getCurrentCaseThrows();
        org.sleuthkit.datamodel.Blackboard tskBlackboard = currentCase.getSleuthkitCase().getBlackboard();
        if (!tskBlackboard.artifactExists(file, BlackboardArtifact.ARTIFACT_TYPE.TSK_INTERESTING_FILE_HIT, attributes)) {
            BlackboardArtifact artifact = file.newArtifact(BlackboardArtifact.ARTIFACT_TYPE.TSK_INTERESTING_FILE_HIT);
            artifact.addAttributes(attributes);
            try {
                currentCase.getServices().getBlackboard().indexArtifact(artifact);
            } catch (Blackboard.BlackboardException ex) {
                logger.log(Level.SEVERE, String.format("Unable to index TSK_INTERESTING_FILE_HIT blackboard artifact %d (file obj_id=%d)", artifact.getArtifactID(), file.getId()), ex);
            }
        }
    } catch (TskCoreException ex) {
        logger.log(Level.SEVERE, String.format("Unable to create TSK_INTERESTING_FILE_HIT artifact for file (obj_id=%d)", file.getId()), ex);
    } catch (NoCurrentCaseException ex) {
        logger.log(Level.SEVERE, "Exception while getting open case.", ex);
    }
}

Long getCountArtifactInstancesByTypeValue(CorrelationAttributeInstance.Type aType,String value){
    String normalizedValue = CorrelationAttributeNormalizer.normalize(aType, value);
    Connection conn = connect();
    Long instanceCount = 0L;
    PreparedStatement preparedStatement = null;
    ResultSet resultSet = null;
    String tableName = EamDbUtil.correlationTypeToInstanceTableName(aType);
    String sql = "SELECT count(*) FROM " + tableName + " WHERE value=?";
    try {
        preparedStatement = conn.prepareStatement(sql);
        preparedStatement.setString(1, normalizedValue);
        resultSet = preparedStatement.executeQuery();
        resultSet.next();
        instanceCount = resultSet.getLong(1);
    } catch (SQLException ex) {
        throw new EamDbException("Error getting count of artifact instances by artifactType and artifactValue.", ex);
    } finally {
        EamDbUtil.closeStatement(preparedStatement);
        EamDbUtil.closeResultSet(resultSet);
        EamDbUtil.closeConnection(conn);
    }
    return instanceCount;
}

void addOrUpdateNodeData(Case autopsyCase,Map<UniquePathKey, OtherOccurrenceNodeInstanceData> nodeDataMap,AbstractFile newFile){
    OtherOccurrenceNodeInstanceData newNode = new OtherOccurrenceNodeInstanceData(newFile, autopsyCase);
    if (newNode.getKnown() != TskData.FileKnown.BAD) {
        List<ContentTag> fileMatchTags = autopsyCase.getServices().getTagsManager().getContentTagsByContent(newFile);
        for (ContentTag tag : fileMatchTags) {
            TskData.FileKnown tagKnownStatus = tag.getName().getKnownStatus();
            if (tagKnownStatus.equals(TskData.FileKnown.BAD)) {
                newNode.updateKnown(TskData.FileKnown.BAD);
                break;
            }
        }
    }
    UniquePathKey uniquePathKey = new UniquePathKey(newNode);
    if (nodeDataMap.containsKey(uniquePathKey)) {
        if (newNode.getKnown() == TskData.FileKnown.BAD) {
            OtherOccurrenceNodeInstanceData prevInstance = nodeDataMap.get(uniquePathKey);
            prevInstance.updateKnown(newNode.getKnown());
        }
    } else {
        nodeDataMap.put(uniquePathKey, newNode);
    }
}

