void onUpdate(){
    if (pollingJob == null || pollingJob.isCancelled()) {
        int refresh;
        try {
            refresh = getConfig().as(OpenSprinklerPiConfig.class).refresh;
        } catch (Exception exp) {
            refresh = this.refreshInterval;
        }
        pollingJob = scheduler.scheduleWithFixedDelay(refreshService, DEFAULT_WAIT_BEFORE_INITIAL_REFRESH, refresh, TimeUnit.SECONDS);
    }
}

