OpenSprinklerApi getHttpApi(String hostname,int port,String password){
    String returnContent;
    int version = -1;
    try {
        returnContent = Http.sendHttpGet(HTTP_REQUEST_URL_PREFIX + hostname + ":" + port + "/" + CMD_OPTIONS_INFO, null);
    } catch (Exception exp) {
        throw new CommunicationApiException("There was a problem in the HTTP communication with the OpenSprinkler API: " + exp.getMessage());
    }
    try {
        version = Parse.jsonInt(returnContent, JSON_OPTION_FIRMWARE_VERSION);
    } catch (Exception exp) {
        version = -1;
    }
    if (version >= 210 && version < 213) {
        return new OpenSprinklerHttpApiV210(hostname, port, password);
    } else if (version >= 213) {
        return new OpenSprinklerHttpApiV213(hostname, port, password);
    } else {
        try {
            returnContent = Http.sendHttpGet(HTTP_REQUEST_URL_PREFIX + hostname + ":" + port + "/sn0", null);
        } catch (Exception exp) {
            throw new CommunicationApiException("There was a problem in the HTTP communication with the OpenSprinkler API: " + exp.getMessage());
        }
        if (returnContent == null || (!returnContent.equals("0") && !returnContent.equals("1"))) {
            throw new CommunicationApiException("There was a problem in the HTTP communication with the OpenSprinkler API, Unexpected API response: " + returnContent);
        }
        return new OpenSprinklerHttpApiV100(hostname, port, password);
    }
}

