void visitSubroutine(Label JSR,long id,int nbSubroutines){
    Label stack = this;
    while (stack != null) {
        Label l = stack;
        stack = l.next;
        l.next = null;
        if (JSR != null) {
            if ((l.status & VISITED2) != 0) {
                continue;
            }
            l.status |= VISITED2;
            if ((l.status & RET) != 0) {
                if (!l.inSameSubroutine(JSR)) {
                    Edge e = new Edge();
                    e.info = l.inputStackTop;
                    e.successor = JSR.successors.successor;
                    e.next = l.successors;
                    l.successors = e;
                }
            }
        } else {
            if (l.inSubroutine(id)) {
                continue;
            }
            l.addToSubroutine(id, nbSubroutines);
        }
        Edge e = l.successors;
        while (e != null) {
            if ((l.status & Label.JSR) == 0 || e != l.successors.next) {
                if (e.successor.next == null) {
                    e.successor.next = stack;
                    stack = e.successor;
                }
            }
            e = e.next;
        }
    }
}

void readSystemScopes(JsonReader reader){
    reader.beginArray();
    while (reader.hasNext()) {
        SystemScope scope = new SystemScope();
        reader.beginObject();
        while (reader.hasNext()) {
            switch(reader.peek()) {
                case END_OBJECT:
                    continue;
                case NAME:
                    String name = reader.nextName();
                    if (reader.peek() == JsonToken.NULL) {
                        reader.skipValue();
                    } else if (name.equals("value")) {
                        scope.setValue(reader.nextString());
                    } else if (name.equals("description")) {
                        scope.setDescription(reader.nextString());
                    } else if (name.equals("allowDynReg")) {
                        scope.setRestricted(!reader.nextBoolean());
                    } else if (name.equals("defaultScope")) {
                        scope.setDefaultScope(reader.nextBoolean());
                    } else if (name.equals("structured")) {
                        logger.warn("Found a structured scope, ignoring structure");
                    } else if (name.equals("structuredParameter")) {
                        logger.warn("Found a structured scope, ignoring structure");
                    } else if (name.equals("icon")) {
                        scope.setIcon(reader.nextString());
                    } else {
                        logger.debug("found unexpected entry");
                        reader.skipValue();
                    }
                    break;
                default:
                    logger.debug("Found unexpected entry");
                    reader.skipValue();
                    continue;
            }
        }
        reader.endObject();
        sysScopeRepository.save(scope);
    }
    reader.endArray();
    logger.info("Done reading system scopes");
}

void onStateChange(LxControlState state){
    if (STATE_POSITION.equals(state.getName()) && targetPosition != null && targetPosition > 0 && targetPosition < 1) {
