        Double downValue = getStateValue(STATE_DOWN);
        if (currentPosition != null && upValue != null && downValue != null) {
            if (((upValue == 1) && (currentPosition <= targetPosition)) || ((downValue == 1) && (currentPosition >= targetPosition))) {
