void handleSystemFavoriteNotification(Matcher m,String resp){
    if (m == null) {
        throw new IllegalArgumentException("m (matcher) cannot be null");
    }
    if (m.groupCount() == 3) {
        try {
            final int favoriteId = Integer.parseInt(m.group(1));
            if (favoriteId >= 1 && favoriteId <= 32) {
                final RioFavorite fav = systemFavorites[favoriteId - 1];
                final String key = m.group(2).toLowerCase();
                final String value = m.group(3);
                switch(key) {
                    case FAV_NAME:
                        fav.setName(value);
                        fireUpdate();
                        break;
                    case FAV_VALID:
                        fav.setValid(!"false".equalsIgnoreCase(value));
                        fireUpdate();
                        break;
                    default:
                        logger.warn("Unknown system favorite notification: '{}'", resp);
                        break;
                }
            } else {
                logger.warn("Invalid System Favorite Notification (favorite < 1 or > 32): '{}')", resp);
            }
        } catch (NumberFormatException e) {
            logger.warn("Invalid System Favorite Notification (favorite not a parsable integer): '{}')", resp);
        }
    } else {
        logger.warn("Invalid System Notification response: '{}'", resp);
    }
}

String hash(String data,String key){
    final Mac mac = Mac.getInstance("HMACMD5");
    final SecretKeySpec sKey = new SecretKeySpec(key.getBytes(), "ASCII");
    mac.init(sKey);
    final byte[] bytes = mac.doFinal(data.getBytes());
    final StringBuilder hashBuf = new StringBuilder();
    for (int i = 0; i < bytes.length; i++) {
        final String hex = Integer.toHexString(0xFF & bytes[i]).toUpperCase();
        if (hex.length() == 1) {
            hashBuf.append('0');
        }
        hashBuf.append(hex);
    }
    return hashBuf.toString();
}

