void appendString(String original){
    for (int i = 0, len = original.length(); i < len; ++i) {
        char c = original.charAt(i);
        int ch = c;
        if (ch < 0 || ch >= ESCAPES.length || ESCAPES[ch] == 0) {
            sb.append(c);
            continue;
        }
        int escape = ESCAPES[ch];
        if (escape > 0) {
            sb.append('\\');
            sb.append((char) escape);
        } else {
            unicodeEscape(ch);
        }
    }
}

