void onGroupWrite(AbstractKNXClient client,IndividualAddress source,GroupAddress destination,byte[] asdu){
    logger.debug("onGroupWrite Thing '{}' received a GroupValueWrite telegram from '{}' for destination '{}'", getThing().getUID(), source, destination);
    for (Channel channel : getThing().getChannels()) {
        withKNXType(channel, (selector, configuration) -> {
            InboundSpec listenSpec = selector.getListenSpec(configuration, destination);
            if (listenSpec != null) {
                logger.trace("onGroupWrite Thing '{}' processes a GroupValueWrite telegram for destination '{}' for channel '{}'", getThing().getUID(), destination, channel.getUID());
                if (isControl(channel.getUID())) {
                    logger.trace("onGroupWrite isControl");
                    Type type = typeHelper.toType(new CommandDP(destination, getThing().getUID().toString(), 0, listenSpec.getDPT()), asdu);
                    if (type != null) {
                        OutboundSpec commandSpec = selector.getCommandSpec(configuration, typeHelper, type);
                        if (commandSpec != null) {
                            GroupAddress mainGa = getKNXChannelMainGA(channel);
                            if (getRespondingSpecValue(mainGa) != commandSpec.getType()) {
                                if (destination.equals(mainGa)) {
                                    logger.trace("onGroupWrite mainGA groupAddressesWriteExposeOnce: '{}'", destination);
                                    groupAddressesWriteBlockedOnce.add(destination);
                                }
                                processDataReceived(destination, asdu, listenSpec, channel.getUID());
                            }
                            rememberRespondingSpec(commandSpec, true);
                        }
                    }
                } else {
                    processDataReceived(destination, asdu, listenSpec, channel.getUID());
                }
            }
        });
    }
}

