                case ZONE_PAGE:
                    stateChanged(RioConstants.CHANNEL_ZONEPAGE, "ON".equals(value) ? OnOffType.ON : OnOffType.OFF);
                    break;
                case ZONE_SLEEPTIMEREMAINING:
                    try {
                        final int nbr = Integer.parseInt(value);
