void brainDiscovered(NeeoSystemInfo sysInfo,InetAddress ipAddress){
    Objects.requireNonNull(sysInfo, "sysInfo cannot be null");
    Objects.requireNonNull(ipAddress, "ipAddress cannot be null");
    final ServiceContext localContext = context;
    if (localContext != null) {
        final String servletUrl = NeeoUtil.getServletUrl(sysInfo.getHostname());
        if (getServletByUrl(servletUrl) == null) {
            logger.debug("Brain discovered: {} at {} and starting servlet at {}", sysInfo.getHostname(), ipAddress, servletUrl);
            try {
                final NeeoBrainServlet newServlet = NeeoBrainServlet.create(localContext, servletUrl, sysInfo.getHostname(), ipAddress);
                servlets.add(newServlet);
                localContext.getHttpService().registerServlet(servletUrl, newServlet, new Hashtable<String, String>(), localContext.getHttpService().createDefaultHttpContext());
                logger.debug("Started NEEO Listener at {}", servletUrl);
            } catch (NamespaceException | ServletException | IOException e) {
                logger.error("Error during servlet startup", e);
            }
        } else {
            logger.debug("Brain servlet with URL of {} already exists - ignored", servletUrl);
        }
    }
}

