PowermaxBaseMessage getMessageHandler(byte[] message){
    PowermaxBaseMessage msgHandler;
    try {
        PowermaxReceiveType msgType = PowermaxReceiveType.fromCode(message[1]);
        switch(msgType) {
            case ACK:
                msgHandler = new PowermaxAckMessage(message);
                break;
            case TIMEOUT:
                msgHandler = new PowermaxTimeoutMessage(message);
                break;
            case DENIED:
                msgHandler = new PowermaxDeniedMessage(message);
                break;
            case DOWNLOAD_RETRY:
                msgHandler = new PowermaxDownloadRetryMessage(message);
                break;
            case SETTINGS:
            case SETTINGS_ITEM:
                msgHandler = new PowermaxSettingsMessage(message);
                break;
            case INFO:
                msgHandler = new PowermaxInfoMessage(message);
                break;
            case EVENT_LOG:
                msgHandler = new PowermaxEventLogMessage(message);
                break;
            case ZONESNAME:
                msgHandler = new PowermaxZonesNameMessage(message);
                break;
            case STATUS:
                msgHandler = new PowermaxStatusMessage(message);
                break;
            case ZONESTYPE:
                msgHandler = new PowermaxZonesTypeMessage(message);
                break;
            case PANEL:
                msgHandler = new PowermaxPanelMessage(message);
                break;
            case POWERLINK:
                msgHandler = new PowermaxPowerlinkMessage(message);
                break;
            case POWERMASTER:
                msgHandler = new PowermaxPowerMasterMessage(message);
                break;
            default:
                msgHandler = new PowermaxBaseMessage(message);
                break;
        }
    } catch (IllegalArgumentException e) {
        msgHandler = new PowermaxBaseMessage(message);
    }
    return msgHandler;
}

StringType extractStringFromRegisters(ModbusRegisterArray registers,int index,int length,Charset charset){
    if (index * 2 + length > registers.size() * 2) {
        throw new IllegalArgumentException(String.format("Index=%d with length=%d is out-of-bounds given registers of size %d", index, length, registers.size()));
    }
    if (index < 0) {
        throw new IllegalArgumentException("Negative index values are not supported");
    }
    if (length < 0) {
        throw new IllegalArgumentException("Negative string length is not supported");
    }
    byte[] buff = new byte[length];
    int src = index;
    int dest;
    for (dest = 0; dest < length; dest++) {
        byte chr;
        if (dest % 2 == 0) {
            chr = (byte) ((registers.getRegister(src).getValue() >> 8));
        } else {
            chr = (byte) (registers.getRegister(src).getValue() & 0xff);
            src++;
        }
        if (chr == 0) {
            break;
        }
        buff[dest] = chr;
    }
    return new StringType(new String(buff, 0, dest, charset));
}

