String getValue(String code,List<JsonObject> langs,String getValue,String code,JsonObject lang){
    if (lang == null) {
        return null;
    }
    JsonElement e = lang;
    Iterable<String> parts = Splitter.on('.').split(code);
    Iterator<String> it = parts.iterator();
    String value = null;
    while (it.hasNext()) {
        String p = it.next();
        if (e.isJsonObject()) {
            JsonObject o = e.getAsJsonObject();
            if (o.has(p)) {
                e = o.get(p);
                if (!it.hasNext()) {
                    if (e.isJsonPrimitive()) {
                        value = e.getAsString();
                    }
                }
            } else {
                break;
            }
        } else {
            break;
        }
    }
    return value;
}

