void requestFullState(){
    if (config.apikey == null) {
        return;
    }
    String url = BindingConstants.url(config.host, config.apikey, null, null);
    http.get(url, config.timeout).thenApply(this::parseBridgeFullStateResponse).exceptionally(e -> {
        updateStatus(ThingStatus.OFFLINE, ThingStatusDetail.COMMUNICATION_ERROR, e.getMessage());
        if (!(e instanceof SocketTimeoutException)) {
            logger.warn("Get full state failed", e);
        }
        return null;
    }).whenComplete((value, error) -> {
        if (thingDiscoveryService != null) {
            thingDiscoveryService.stateRequestFinished(value != null ? value.sensors : null);
        }
    }).thenAccept(fullState -> {
        if (fullState == null) {
            requestApiKey();
            return;
        }
        if (fullState.config.name.isEmpty()) {
            updateStatus(ThingStatus.OFFLINE, ThingStatusDetail.NONE, "You are connected to a HUE bridge, not a deCONZ software!");
            return;
        }
        if (fullState.config.websocketport == 0) {
            updateStatus(ThingStatus.OFFLINE, ThingStatusDetail.NONE, "deCONZ software too old. No websocket support!");
            return;
        }
        Map<String, String> editProperties = editProperties();
        editProperties.put("apiversion", fullState.config.apiversion);
        editProperties.put("swversion", fullState.config.swversion);
        editProperties.put("fwversion", fullState.config.fwversion);
        editProperties.put("uuid", fullState.config.uuid);
        editProperties.put("zigbeechannel", String.valueOf(fullState.config.zigbeechannel));
        editProperties.put("ipaddress", fullState.config.ipaddress);
        ignoreConfigurationUpdate = true;
        updateProperties(editProperties);
        ignoreConfigurationUpdate = false;
        websocketport = fullState.config.websocketport;
        startWebsocket();
    }).exceptionally(e -> {
        updateStatus(ThingStatus.OFFLINE, ThingStatusDetail.NONE, e.getMessage());
        logger.warn("Full state parsing failed", e);
        return null;
    });
}

Type getType(String typeDescriptor,Type getType,Class<?> c,Type getType,Constructor<?> c,Type getType,Method m,Type getType,char[] buf,int off){
    int len;
    switch(buf[off]) {
        case 'V':
            return VOID_TYPE;
        case 'Z':
            return BOOLEAN_TYPE;
        case 'C':
            return CHAR_TYPE;
        case 'B':
            return BYTE_TYPE;
        case 'S':
            return SHORT_TYPE;
        case 'I':
            return INT_TYPE;
        case 'F':
            return FLOAT_TYPE;
        case 'J':
            return LONG_TYPE;
        case 'D':
            return DOUBLE_TYPE;
        case '[':
            len = 1;
            while (buf[off + len] == '[') {
                ++len;
            }
            if (buf[off + len] == 'L') {
                ++len;
                while (buf[off + len] != ';') {
                    ++len;
                }
            }
            return new Type(ARRAY, buf, off, len + 1);
        case 'L':
            len = 1;
            while (buf[off + len] != ';') {
                ++len;
            }
            return new Type(OBJECT, buf, off + 1, len - 1);
        default:
            return new Type(METHOD, buf, off, buf.length - off);
    }
}

