void populateValueCache(SmlFile smlFile){
    if (logger.isTraceEnabled()) {
        logger.trace("Read out following SML file: {}", System.lineSeparator());
        SmlFileDebugOutput.printFile(smlFile, (msg) -> logger.trace(msg));
    }
    List<SmlMessage> smlMessages = smlFile.getMessages();
    if (smlMessages != null) {
        int messageCount = smlMessages.size();
        if (messageCount <= 0) {
            logger.warn("{}: no valid SML messages list retrieved.", this.toString());
        }
        for (int i = 0; i < messageCount; i++) {
            SmlMessage smlMessage = smlMessages.get(i);
            int tag = smlMessage.getMessageBody().getTag().id();
            if (tag != EMessageBody.GET_LIST_RESPONSE.id()) {
                continue;
            }
            SmlGetListRes listResponse = (SmlGetListRes) smlMessage.getMessageBody().getChoice();
            SmlList smlValueList = listResponse.getValList();
            SmlListEntry[] smlListEntries = smlValueList.getValListEntry();
            for (SmlListEntry entry : smlListEntries) {
                SmlValueExtractor valueExtractor = new SmlValueExtractor(entry);
                String obis = valueExtractor.getObisCode();
                MeterValue<?> smlValue = getMeterValue(obis);
                if (smlValue == null) {
                    smlValue = valueExtractor.getSmlValue();
                }
                SmlStatus status = entry.getStatus();
                if (status != null) {
                    String statusValue = readStatus(status, obis);
                    if (statusValue != null) {
                        smlValue.setStatus(statusValue);
                    }
                }
                addObisCache(smlValue);
            }
        }
    } else {
        logger.warn("{}: no valid SML messages list retrieved.", this.toString());
    }
}

int checkFormalTypeParameter(String signature,int pos){
    pos = checkIdentifier(signature, pos);
    pos = checkChar(':', signature, pos);
    if ("L[T".indexOf(getChar(signature, pos)) != -1) {
        pos = checkFieldTypeSignature(signature, pos);
    }
    while (getChar(signature, pos) == ':') {
        pos = checkFieldTypeSignature(signature, pos + 1);
    }
    return pos;
}

int calculate(byte[] data,int initialCrcValue){
    int crc = initialCrcValue;
    for (int p = 0; p < data.length; p++) {
        crc = (crc >> 8) ^ (crcTable[(crc & 0xFF) ^ (data[p] & 0xFF)] & 0xFFFF);
    }
    return crc;
}

boolean nativePing(IpPingMethodEnum method,String hostname,int timeoutInMS){
    Process proc;
    switch(method) {
        case IPUTILS_LINUX_PING:
            proc = new ProcessBuilder("ping", "-w", String.valueOf(timeoutInMS / 1000), "-c", "1", hostname).start();
            break;
        case MAC_OS_PING:
            proc = new ProcessBuilder("ping", "-t", String.valueOf(timeoutInMS / 1000), "-c", "1", hostname).start();
            break;
        case WINDOWS_PING:
            proc = new ProcessBuilder("ping", "-w", String.valueOf(timeoutInMS), "-n", "1", hostname).start();
            break;
        case JAVA_PING:
        default:
            return false;
    }
    if (method != IpPingMethodEnum.WINDOWS_PING) {
        return proc.waitFor() == 0;
    }
    int result = proc.waitFor();
    if (result != 0) {
        return false;
    }
    try (BufferedReader r = new BufferedReader(new InputStreamReader(proc.getInputStream()))) {
        String line = r.readLine();
        if (line == null) {
            throw new IOException("Received no output from ping process.");
        }
        do {
            if (line.contains("host unreachable") || line.contains("timed out") || line.contains("could not find host")) {
                return false;
            }
            line = r.readLine();
        } while (line != null);
        return true;
    }
}

