boolean merge(ClassWriter cw,Frame frame,int edge,boolean merge,ClassWriter cw,int t,int[] types,int index){
    int u = types[index];
    if (u == t) {
        return false;
    }
    if ((t & ~DIM) == NULL) {
        if (u == NULL) {
            return false;
        }
        t = NULL;
    }
    if (u == 0) {
        types[index] = t;
        return true;
    }
    int v;
    if ((u & BASE_KIND) == OBJECT || (u & DIM) != 0) {
        if (t == NULL) {
            return false;
        } else if ((t & (DIM | BASE_KIND)) == (u & (DIM | BASE_KIND))) {
            if ((u & BASE_KIND) == OBJECT) {
                v = (t & DIM) | OBJECT | cw.getMergedType(t & BASE_VALUE, u & BASE_VALUE);
            } else {
                v = OBJECT | cw.addType("java/lang/Object");
            }
        } else if ((t & BASE_KIND) == OBJECT || (t & DIM) != 0) {
            v = OBJECT | cw.addType("java/lang/Object");
        } else {
            v = TOP;
        }
    } else if (u == NULL) {
        v = (t & BASE_KIND) == OBJECT || (t & DIM) != 0 ? t : TOP;
    } else {
        v = TOP;
    }
    if (u != v) {
        types[index] = v;
        return true;
    }
    return false;
}

