void initGetStatusAndKeepAliveThread(){
    if (this.keepAliveJob != null) {
        this.keepAliveJob.cancel(true);
    }
    Runnable runnable = () -> {
        logger.debug("Begin of Socket keep alive thread routine. Current configuration update interval: {} seconds.", SilvercrestWifiSocketHandler.this.updateInterval);
        long now = System.currentTimeMillis();
        long timePassedFromLastUpdateInSeconds = (now - SilvercrestWifiSocketHandler.this.latestUpdate) / 1000;
        logger.trace("Latest Update: {} Now: {} Delta: {} seconds", SilvercrestWifiSocketHandler.this.latestUpdate, now, timePassedFromLastUpdateInSeconds);
        logger.debug("It has been passed {} seconds since the last update on socket with mac address {}.", timePassedFromLastUpdateInSeconds, SilvercrestWifiSocketHandler.this.macAddress);
        boolean mustUpdateHostAddress = timePassedFromLastUpdateInSeconds > (SilvercrestWifiSocketHandler.this.updateInterval * 2);
        if (mustUpdateHostAddress) {
            logger.debug("No updates have been received for a long time, search the mac address {} in network...", SilvercrestWifiSocketHandler.this.getMacAddress());
            SilvercrestWifiSocketHandler.this.lookupForSocketHostAddress();
        }
        boolean considerThingOffline = (SilvercrestWifiSocketHandler.this.latestUpdate < 0) || (timePassedFromLastUpdateInSeconds > (SilvercrestWifiSocketHandler.this.updateInterval * 4));
        if (considerThingOffline) {
            logger.debug("No updates have been received for a long long time will put the thing with mac address {} OFFLINE.", SilvercrestWifiSocketHandler.this.getMacAddress());
            SilvercrestWifiSocketHandler.this.updateStatus(ThingStatus.OFFLINE);
        }
        SilvercrestWifiSocketHandler.this.sendCommand(SilvercrestWifiSocketRequestType.GPIO_STATUS);
    };
    this.keepAliveJob = this.scheduler.scheduleWithFixedDelay(runnable, 1, SilvercrestWifiSocketHandler.this.updateInterval, TimeUnit.SECONDS);
}

void box(Type type){
    if (type.getSort() == Type.OBJECT || type.getSort() == Type.ARRAY) {
        return;
    }
    if (type == Type.VOID_TYPE) {
        push((String) null);
    } else {
        Type boxed = getBoxedType(type);
        newInstance(boxed);
        if (type.getSize() == 2) {
            dupX2();
            dupX2();
            pop();
        } else {
            dupX1();
            swap();
        }
        invokeConstructor(boxed, new Method("<init>", Type.VOID_TYPE, new Type[] { type }));
    }
}

