double getSlopeRate(Tile t){
    Point2D p = t.getCoord();
    if (t.ramp != this) {
        return 0;
    }
    if (angle == 0) {
        if (p.x > maxX) {
            return 1;
        } else if (p.x < minX) {
            return 0;
        } else {
            return (double) (p.x - minX) / (maxX - minX + 1);
        }
    } else if (angle == AngleUtil.FLAT) {
        if (p.x > maxX) {
            return 0;
        } else if (p.x < minX) {
            return 1;
        } else {
            return (double) (maxX - p.x + 1) / (maxX - minX + 1);
        }
    } else if (angle == AngleUtil.RIGHT) {
        if (p.y > maxY) {
            return 1;
        } else if (p.y < minY) {
            return 0;
        } else {
            return (double) (p.y - minY) / (maxY - minY + 1);
        }
    } else if (angle == -AngleUtil.RIGHT) {
        if (p.y > maxY) {
            return 0;
        } else if (p.y < minY) {
            return 1;
        } else {
            return (double) (maxY - p.y + 1) / (maxY - minY + 1);
        }
    }
    throw new RuntimeException();
}

