boolean sendCommand(){
    LinkedBlockingQueue<ResponseMessage> responseQueue = new LinkedBlockingQueue<ResponseMessage>(RESPONSE_QUEUE_MAX_DEPTH);
    RequestMessage requestMsg = new RequestMessage(commandName, commandType, deviceCommand, responseQueue);
    try {
        requestQueue.put(requestMsg);
        logger.trace("Put request on queue (depth={}), sent command '{}'", requestQueue.size(), deviceCommand);
        ResponseMessage responseMsg = responseQueue.poll(RESPONSE_QUEUE_TIMEOUT, TimeUnit.MILLISECONDS);
        if (responseMsg == null) {
            createGenericError("Timed out waiting on response queue for message");
            return false;
        }
        deviceReply = responseMsg.getDeviceReply();
        logger.trace("Got response message off response queue, received reply '{}'", deviceReply);
        if (isErrorReply(deviceReply)) {
            return false;
        }
    } catch (InterruptedException e) {
        createGenericError("Poll of response queue was interrupted");
        return false;
    }
    return true;
}

void readSystemScopes(JsonReader reader){
    reader.beginArray();
    while (reader.hasNext()) {
        SystemScope scope = new SystemScope();
        reader.beginObject();
        while (reader.hasNext()) {
            switch(reader.peek()) {
                case END_OBJECT:
                    continue;
                case NAME:
                    String name = reader.nextName();
                    if (reader.peek() == JsonToken.NULL) {
                        reader.skipValue();
                    } else if (name.equals(VALUE)) {
                        scope.setValue(reader.nextString());
                    } else if (name.equals(DESCRIPTION)) {
                        scope.setDescription(reader.nextString());
                    } else if (name.equals(RESTRICTED)) {
                        scope.setRestricted(reader.nextBoolean());
                    } else if (name.equals(DEFAULT_SCOPE)) {
                        scope.setDefaultScope(reader.nextBoolean());
                    } else if (name.equals(ICON)) {
                        scope.setIcon(reader.nextString());
                    } else {
                        logger.debug("found unexpected entry");
                        reader.skipValue();
                    }
                    break;
                default:
                    logger.debug("Found unexpected entry");
                    reader.skipValue();
                    continue;
            }
        }
        reader.endObject();
        sysScopeRepository.save(scope);
    }
    reader.endArray();
    logger.info("Done reading system scopes");
}

