void handleGet(HttpServletRequest req,String[] paths,HttpServletResponse resp){
    Objects.requireNonNull(req, "req cannot be null");
    Objects.requireNonNull(paths, "paths cannot be null");
    Objects.requireNonNull(resp, "resp cannot be null");
    try {
        if (StringUtils.equalsIgnoreCase(paths[0], "brainstatus")) {
            final List<BrainStatus> status = new ArrayList();
            for (NeeoBrainServlet servlet : service.getServlets()) {
                status.add(servlet.getBrainStatus());
            }
            NeeoUtil.write(resp, gson.toJson(status));
        } else if (StringUtils.equalsIgnoreCase(paths[0], "blinkled")) {
            final String brainId = req.getParameter("brainid");
            final NeeoBrainServlet servlet = service.getServlet(brainId);
            if (servlet == null) {
                NeeoUtil.write(resp, gson.toJson(new ReturnStatus("Unknown BraidID: " + brainId)));
            } else {
                try {
                    servlet.getBrainApi().blinkLed();
                    NeeoUtil.write(resp, gson.toJson(new ReturnStatus(true)));
                } catch (IOException e) {
                    NeeoUtil.write(resp, gson.toJson(new ReturnStatus("Exception occurred blinking LED: " + e.getMessage())));
                }
            }
        } else if (StringUtils.equalsIgnoreCase(paths[0], "getlog")) {
            final String brainId = req.getParameter("brainid");
            final NeeoBrainServlet servlet = service.getServlet(brainId);
            if (servlet == null) {
                NeeoUtil.write(resp, gson.toJson(new ReturnStatus("Unknown BraidID: " + brainId)));
            } else {
                try {
                    final String log = servlet.getBrainApi().getLog();
                    NeeoUtil.write(resp, gson.toJson(new ReturnStatus(true, log)));
                } catch (IOException e) {
                    NeeoUtil.write(resp, gson.toJson(new ReturnStatus("Exception occurred getting log: " + e.getMessage())));
                }
            }
        } else {
            logger.debug("Unknown get path: {}", StringUtils.join(paths, ','));
        }
    } catch (JsonParseException | IllegalArgumentException | NullPointerException e) {
        logger.debug("Exception handling get: {}", e.getMessage(), e);
        NeeoUtil.write(resp, gson.toJson(new ReturnStatus(e.getMessage())));
    }
}

void processDSCAlarmMessage(){
    DSCAlarmCode dscAlarmCode;
    if (message.length() > 3) {
        try {
            if (message.length() >= 8 && message.charAt(2) == ':' && message.charAt(5) == ':') {
                timeStamp = message.substring(0, 8);
                message = message.substring(9, message.length() - 2);
            } else {
                message = message.substring(0, message.length() - 2);
            }
            codeReceived = message.substring(0, 3);
            if (message.length() >= 4) {
                data = message.substring(3);
            }
        } catch (Exception e) {
            logger.error("processDSCAlarmMessage(): Error processing message: ({}) ", message, e);
            return;
        }
        dscAlarmCode = DSCAlarmCode.getDSCAlarmCodeValue(codeReceived);
        if (dscAlarmCode != null) {
            name = dscAlarmCode.getName();
            description = dscAlarmCode.getDescription();
            MessageParameters messageParms = DSCALARM_MESSAGE_PARAMETERS.get(dscAlarmCode);
            if (messageParms != null) {
                boolean hasPartition = messageParms.hasPartition();
                boolean hasZone = messageParms.hasZone();
                if (hasPartition) {
                    partition = message.substring(3, 4);
                }
                if (hasZone) {
                    if (hasPartition) {
                        zone = message.substring(4);
                    } else {
                        zone = message.substring(3);
                    }
                }
                messageType = messageParms.getType();
            }
            switch(dscAlarmCode) {
                case SystemError:
                    int systemErrorCode = 0;
                    systemErrorCode = Integer.parseInt(data);
                    switch(systemErrorCode) {
                        case 1:
                            error = "Receive Buffer Overrun";
                            break;
                        case 2:
                            error = "Receive Buffer Overflow";
                            break;
                        case 3:
                            error = "Transmit Buffer Overflow";
                            break;
                        case 10:
                            error = "Keybus Transmit Buffer Overrun";
                            break;
                        case 11:
                            error = "Keybus Transmit Time Timeout";
                            break;
                        case 12:
                            error = "Keybus Transmit Mode Timeout";
                            break;
                        case 13:
                            error = "Keybus Transmit Keystring Timeout";
                            break;
                        case 14:
                            error = "Keybus Interface Not Functioning";
                            break;
                        case 15:
                            error = "Keybus Busy - Attempting to Disarm or Arm with user code";
                            break;
                        case 16:
                            error = "Keybus Busy ? Lockout";
                            break;
                        case 17:
                            error = "Keybus Busy ? Installers Mode";
                            break;
                        case 18:
                            error = "Keybus Busy - General Busy";
                            break;
                        case 20:
                            error = "API Command Syntax Error";
                            break;
                        case 21:
                            error = "API Command Partition Error - Requested Partition is out of bounds";
                            break;
                        case 22:
                            error = "API Command Not Supported";
                            break;
                        case 23:
                            error = "API System Not Armed - Sent in response to a disarm command";
                            break;
                        case 24:
                            error = "API System Not Ready to Arm - System is either not-secure, in exit-delay, or already armed";
                            break;
                        case 25:
                            error = "API Command Invalid Length";
                            break;
                        case 26:
                            error = "API User Code not Required";
                            break;
                        case 27:
                            error = "API Invalid Characters in Command - No alpha characters are allowed except for checksum";
                            break;
                        case 28:
                            error = "API Virtual Keypad is Disabled";
                            break;
                        case 29:
                            error = "API Not Valid Parameter";
                            break;
                        case 30:
                            error = "API Keypad Does Not Come Out of Blank Mode";
                            break;
                        case 31:
                            error = "API IT-100 is Already in Thermostat Menu";
                            break;
                        case 32:
                            error = "API IT-100 is NOT in Thermostat Menu";
                            break;
                        case 33:
                            error = "API No Response From Thermostat or Escort Module";
                            break;
                        case 0:
                        default:
                            error = "No Error";
                            break;
                    }
                    break;
                case PartitionArmed:
                    mode = message.substring(4);
                    if (mode.equals("0")) {
                        name += " (Away)";
                    } else if (mode.equals("1")) {
                        name += " (Stay)";
                    } else if (mode.equals("2")) {
                        name += " (ZEA)";
                    } else if (mode.equals("3")) {
                        name += " (ZES)";
                    }
                    messageType = DSCAlarmMessageType.PARTITION_EVENT;
                    break;
                case UserClosing:
                    user = message.substring(4);
                    name = name.concat(": " + user);
                    description = codeReceived + ": Partition " + String.valueOf(partition) + " has been armed by user " + user + ".";
                    messageType = DSCAlarmMessageType.PARTITION_EVENT;
                    break;
                case UserOpening:
                    user = message.substring(4);
                    name = name.concat(": " + user);
                    description = codeReceived + ": Partition " + String.valueOf(partition) + " has been disarmed by user " + user + ".";
                    messageType = DSCAlarmMessageType.PARTITION_EVENT;
                    break;
                default:
                    break;
            }
            logger.debug("parseAPIMessage(): Message Received ({}) - Code: {}, Name: {}, Description: {}, Data: {}\r\n", message, codeReceived, name, description, data);
        }
    } else {
        codeReceived = "-1";
        data = "";
        dscAlarmCode = DSCAlarmCode.getDSCAlarmCodeValue(codeReceived);
        name = dscAlarmCode.getName();
        description = dscAlarmCode.getDescription();
        logger.debug("parseAPIMessage(): Invalid Message Received");
    }
}

int readAnnotationValues(int v,char[] buf,boolean named,AnnotationVisitor av){
    int i = readUnsignedShort(v);
    v += 2;
    if (named) {
        for (; i > 0; --i) {
            v = readAnnotationValue(v + 2, buf, readUTF8(v, buf), av);
        }
    } else {
        for (; i > 0; --i) {
            v = readAnnotationValue(v, buf, null, av);
        }
    }
    if (av != null) {
        av.visitEnd();
    }
    return v;
}

