Triplet<X, X, X> fromIterable(Iterable<X> iterable,Triplet<X, X, X> fromIterable,Iterable<X> iterable,int index,Triplet<X, X, X> fromIterable,Iterable<X> iterable,int index,boolean exactSize){
    if (iterable == null) {
        throw new IllegalArgumentException("Iterable cannot be null");
    }
    boolean tooFewElements = false;
    X element0 = null;
    X element1 = null;
    X element2 = null;
    final Iterator<X> iter = iterable.iterator();
    int i = 0;
    while (i < index) {
        if (iter.hasNext()) {
            iter.next();
        } else {
            tooFewElements = true;
        }
        i++;
    }
    if (iter.hasNext()) {
        element0 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element1 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element2 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (tooFewElements && exactSize) {
        throw new IllegalArgumentException("Not enough elements for creating a Triplet (3 needed)");
    }
    if (iter.hasNext() && exactSize) {
        throw new IllegalArgumentException("Iterable must have exactly 3 available elements in order to create a Triplet.");
    }
    return new Triplet<X, X, X>(element0, element1, element2);
}

