int getGraphWidth(){
    if (graphWidthInPixels > 0) {
        return graphWidthInPixels;
    }
    int width;
    float w = getWidth();
    if (w < 0) {
        return DEFAULT_WIDTH;
    }
    switch(getWidthUnits()) {
        case CM:
            width = (int) (w * 96 / 2.54);
            break;
        case INCH:
            width = (int) (w * 96);
            break;
        case PERCENTAGE:
            width = DEFAULT_WIDTH;
            break;
        default:
            width = (int) w;
            break;
    }
    return width;
}

void sendSearchForBroadcast(DatagramSocket datagramSocket){
    byte[] t = new byte[] { (byte) 0x10, (byte) 0x00, (byte) 0x00, (byte) 0x00, (byte) 0x0A, (byte) 0x02, clientSID1, clientSID2, (byte) 0x01, bridgeMAC[0], bridgeMAC[1], bridgeMAC[2], bridgeMAC[3], bridgeMAC[4], bridgeMAC[5] };
    if (lastKnownIP != null) {
        try {
            datagramSocket.send(new DatagramPacket(t, t.length, lastKnownIP, port));
        } catch (IOException e) {
            logger.warn("Could not send discover packet! {}", e.getLocalizedMessage());
        }
        return;
    }
    Enumeration<NetworkInterface> enumNetworkInterfaces;
    try {
        enumNetworkInterfaces = NetworkInterface.getNetworkInterfaces();
    } catch (SocketException socketException) {
        logger.warn("Could not enumerate network interfaces for sending the discover packet!", socketException);
        return;
    }
    DatagramPacket packet = new DatagramPacket(t, t.length, lastKnownIP, port);
    while (enumNetworkInterfaces.hasMoreElements()) {
        NetworkInterface networkInterface = enumNetworkInterfaces.nextElement();
        Iterator<InterfaceAddress> it = networkInterface.getInterfaceAddresses().iterator();
        while (it.hasNext()) {
            InterfaceAddress address = it.next();
            if (address == null) {
                continue;
            }
            InetAddress broadcast = address.getBroadcast();
            if (broadcast != null && !address.getAddress().isLoopbackAddress()) {
                packet.setAddress(broadcast);
                try {
                    datagramSocket.send(packet);
                } catch (IOException e) {
                    logger.warn("Could not send discovery packet! {}", e.getLocalizedMessage());
                }
            }
        }
    }
}

