Octet<X, X, X, X, X, X, X, X> fromIterable(Iterable<X> iterable,Octet<X, X, X, X, X, X, X, X> fromIterable,Iterable<X> iterable,int index,Octet<X, X, X, X, X, X, X, X> fromIterable,Iterable<X> iterable,int index,boolean exactSize){
    if (iterable == null) {
        throw new IllegalArgumentException("Iterable cannot be null");
    }
    boolean tooFewElements = false;
    X element0 = null;
    X element1 = null;
    X element2 = null;
    X element3 = null;
    X element4 = null;
    X element5 = null;
    X element6 = null;
    X element7 = null;
    final Iterator<X> iter = iterable.iterator();
    int i = 0;
    while (i < index) {
        if (iter.hasNext()) {
            iter.next();
        } else {
            tooFewElements = true;
        }
        i++;
    }
    if (iter.hasNext()) {
        element0 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element1 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element2 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element3 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element4 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element5 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element6 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element7 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (tooFewElements && exactSize) {
        throw new IllegalArgumentException("Not enough elements for creating an Octet (8 needed)");
    }
    if (iter.hasNext() && exactSize) {
        throw new IllegalArgumentException("Iterable must have exactly 8 available elements in order to create an Octet.");
    }
    return new Octet<X, X, X, X, X, X, X, X>(element0, element1, element2, element3, element4, element5, element6, element7);
}

