String evalSimpleExp(String v,Map<String, String> vars){
    int p = v.indexOf("#{");
    if (p > -1) {
        int s = 0;
        StringBuilder sb = new StringBuilder();
        while (p > -1) {
            if (p != s) {
                sb.append(v.substring(s, p));
            }
            s = v.indexOf('}', p);
            if (s < 0) {
                sb.append(v.substring(p));
                break;
            }
            String ek = v.substring(p + 2, s);
            String ev = vars.get(ek);
            if (ev == null) {
                ev = vars.get("system." + ek);
                if (ev == null) {
                    ev = System.getProperty(ek);
                }
            }
            sb.append(ev);
            s++;
            p = v.indexOf("#{", s);
        }
        if (p < 0 && s != v.length()) {
            sb.append(v.substring(s));
        }
        return sb.toString();
    }
    return v;
}

