boolean meetObstacle(Map map,Point2D p1,Point2D p2){
    double dirX = p2.x - p1.x;
    double dirY = p2.y - p1.y;
    double length = Math.sqrt(dirX * dirX + dirY * dirY);
    dirX /= length;
    dirY /= length;
    double tDeltaX = 1 / Math.abs(dirX);
    double tDeltaY = 1 / Math.abs(dirY);
    int x = (int) Math.floor(p1.x);
    int y = (int) Math.floor(p1.y);
    int endX = (int) Math.floor(p2.x);
    int endY = (int) Math.floor(p2.y);
    int stepX = (int) Math.signum(dirX);
    int stepY = (int) Math.signum(dirY);
    double tMaxX, tMaxY;
    if (dirX < 0) {
        tMaxX = (x - p1.x) / dirX;
    } else {
        tMaxX = (x + 1 - p1.x) / dirX;
    }
    if (dirY < 0) {
        tMaxY = (y - p1.y) / dirY;
    } else {
        tMaxY = (y + 1 - p1.y) / dirY;
    }
    if (map.get(x, y).isBlocked()) {
        return true;
    }
    boolean reachedX = false, reachedY = false;
    while (!reachedX || !reachedY) {
        if (tMaxX < tMaxY) {
            tMaxX += tDeltaX;
            x += stepX;
        } else {
            tMaxY += tDeltaY;
            y += stepY;
        }
        if (map.get(x, y).isBlocked()) {
            return true;
        }
        if (stepX > 0) {
            if (x >= endX) {
                reachedX = true;
            }
        } else if (x <= endX) {
            reachedX = true;
        }
        if (stepY > 0) {
            if (y >= endY) {
                reachedY = true;
            }
        } else if (y <= endY) {
            reachedY = true;
        }
    }
    return false;
}

ArrayList<Polygon> getSplits(Line2D splitLine,ArrayList<Polygon> getSplits,Point2D pointOnLeftSide,Line2D splitLine){
    PointRing left = new PointRing();
    PointRing right = new PointRing();
    PointRing actual = left;
    if (!points.contains(pointOnLeftSide))
        throw new RuntimeException("The specified point can't be found in this polygon.");
    Point2D p = pointOnLeftSide;
    Point2D start = null;
    for (int i = 0; i < size(); i++) {
        actual.add(p);
        Point2D next = points.getNext(p);
        Segment2D edge = new Segment2D(p, next);
        if (edge.isCollinear(splitLine))
            throw new RuntimeException("Trying to split a polygon with a line collinear to one of its edges : " + splitLine);
        if (edge.intersect(splitLine)) {
            Point2D intersection = edge.getAnyIntersection(splitLine);
            if (!intersection.equals(edge.getEnd())) {
                if (!intersection.equals(edge.getStart()))
                    actual.add(intersection);
                if (actual == left) {
                    start = p;
                    actual = right;
                } else
                    actual = left;
                actual.add(intersection);
            }
        }
        p = next;
    }
    if (actual == right)
        throw new RuntimeException("The polygon have been splitted on an impair number of points, which is not allowed.");
    left.shiftTo(start);
    ArrayList<Polygon> res = new ArrayList<Polygon>();
    res.add(new Polygon(left));
    res.add(new Polygon(right));
    return res;
}

void handlePresetNotification(Matcher m,String resp){
    if (m == null) {
        throw new IllegalArgumentException("m (matcher) cannot be null");
    }
    if (m.groupCount() == 5) {
        try {
            final int source = Integer.parseInt(m.group(1));
            if (source >= 1 && source <= 8) {
                final int bank = Integer.parseInt(m.group(2));
                if (bank >= 1 && bank <= 6) {
                    final int preset = Integer.parseInt(m.group(3));
                    if (preset >= 1 && preset <= 6) {
                        final String key = m.group(4).toLowerCase();
                        final String value = m.group(5);
                        final RioPreset rioPreset = presets[source - 1][(bank - 1) * 6 + preset - 1];
                        switch(key) {
                            case PRESET_NAME:
                                rioPreset.setName(value);
                                fireUpdate(source);
                                break;
                            case PRESET_VALID:
                                rioPreset.setValid(!"false".equalsIgnoreCase(value));
                                fireUpdate(source);
                                break;
                            default:
                                logger.warn("Unknown preset notification: '{}'", resp);
                                break;
                        }
                    } else {
                        logger.debug("Preset ID must be between 1 and 6: {}", resp);
                    }
                } else {
                    logger.debug("Bank ID must be between 1 and 6: {}", resp);
                }
            } else {
                logger.debug("Source ID must be between 1 and 8: {}", resp);
            }
        } catch (NumberFormatException e) {
            logger.warn("Invalid Preset Notification (source/bank/preset not a parsable integer): '{}')", resp);
        }
    } else {
        logger.warn("Invalid Preset Notification: '{}')", resp);
    }
}

