            currentScannedPortName = portIdentifier.getName();
            if (portIdentifier.isCurrentlyOwned()) {
                logger.trace("Possible port to check:{}, owned:{} by:{}", currentScannedPortName, portIdentifier.isCurrentlyOwned(), portIdentifier.getCurrentOwner());
                if (DSMRBindingConstants.DSMR_PORT_NAME.equals(portIdentifier.getCurrentOwner())) {
                    logger.debug("The port {} is owned by this binding. If no DSMR meters will be found it " + "might indicate the port is locked by an older instance of this binding. " + "Restart the system to unlock the port.", currentScannedPortName);
                }
            } else {
                logger.debug("Start discovery on serial port: {}", currentScannedPortName);
                DSMRSerialAutoDevice device = new DSMRSerialAutoDevice(serialPortManager, portIdentifier.getName(), this, scheduler, BAUDRATE_SWITCH_TIMEOUT_SECONDS);
                device.setLenientMode(true);
