    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element1 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element2 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element3 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (iter.hasNext()) {
        element4 = iter.next();
    } else {
        tooFewElements = true;
    }
    if (tooFewElements && exactSize) {
        throw new IllegalArgumentException("Not enough elements for creating a Quintet (5 needed)");
    }
    if (iter.hasNext() && exactSize) {
        throw new IllegalArgumentException("Iterable must have exactly 5 available elements in order to create a Quintet.");
    }
    return new Quintet<X, X, X, X, X>(element0, element1, element2, element3, element4);
}

void sendData(int[] data){
    int calcChecksum = 0;
    int checksumResponse = -1;
    for (int i = 0; i < data.length; i++) {
        calcChecksum = (calcChecksum + (0x000000FF & (data[i]))) & 0x000000FF;
        logger.trace("Checksum calc: int {} = {}", i, Integer.toHexString(data[i]));
    }
    if (connect()) {
        synchronized (serialPort) {
            long startTime = System.currentTimeMillis();
            serialPort.notifyOnDataAvailable(false);
            try {
                int retryCount = 0;
                while (checksumResponse != calcChecksum) {
                    retryCount++;
                    for (int i = 0; i < data.length; i++) {
                        serialOutput.write(data[i]);
                        serialOutput.flush();
                    }
                    long sendTime = System.currentTimeMillis();
                    logger.trace("Sent the following data out the serial port in {} msec, {}", (sendTime - startTime), Arrays.toString(data));
                    checksumResponse = serialInput.readUnsignedByte();
                    logger.trace("Attempted to send data, try number: {} Checksum expected: {} received: {}", retryCount, Integer.toHexString(calcChecksum), Integer.toHexString(checksumResponse));
                    long ckSumTime = System.currentTimeMillis();
                    logger.trace("Received serial port check sum in {} msec", (ckSumTime - sendTime));
                    if (checksumResponse != calcChecksum) {
                        processRequestFromIFace(checksumResponse);
                        if (retryCount > IO_MAX_SEND_RETRY_COUNT) {
                            logger.warn("Failed to send data to X10 hardware due to too many checksum failures");
                            serialPort.notifyOnDataAvailable(true);
                            throw new IOException("Max retries exceeded");
                        }
                    }
                }
                logger.trace("Data transmission to interface was successful, sending ACK.  X10 transmission over powerline will now commence.");
                long ackTime = System.currentTimeMillis();
                serialOutput.write(CHECKSUM_ACK);
                serialOutput.flush();
                int response = serialInput.readUnsignedByte();
                if (response == IF_READY) {
                    long cmpltdTime = System.currentTimeMillis();
                    logger.trace("Serial port X10 ACK completed in {} msec, TOTAL X10 TRANSMISSION TIME in {} ms", (cmpltdTime - ackTime), (cmpltdTime - startTime));
                } else {
                    logger.warn("Expected IF_READY ({}) response from hardware but received: {} instead", Integer.toHexString(IF_READY & 0x00000FF), Integer.toHexString(response & 0x00000FF));
                }
            } catch (EOFException ex) {
                logger.warn("Received EOF exception while sending X10 command after {} ms. Make sure the cm11a is connected to the serial port", (System.currentTimeMillis() - startTime));
            }
            serialPort.notifyOnDataAvailable(true);
        }
    }
}

