void handleCommand(ChannelUID channelUID,Command command){
    if (REFRESH.equals(command)) {
        Thermostat lastUpdate = getLastUpdate();
        if (lastUpdate != null) {
            updateState(channelUID, getChannelState(channelUID, lastUpdate));
        }
    } else if (CHANNEL_FAN_TIMER_ACTIVE.equals(channelUID.getId())) {
        if (command instanceof OnOffType) {
            addUpdateRequest("fan_timer_active", command == OnOffType.ON);
        }
    } else if (CHANNEL_FAN_TIMER_DURATION.equals(channelUID.getId())) {
        if (command instanceof QuantityType) {
            QuantityType<Time> minuteQuantity = ((QuantityType<Time>) command).toUnit(SmartHomeUnits.MINUTE);
            if (minuteQuantity != null) {
                addUpdateRequest("fan_timer_duration", minuteQuantity.intValue());
            }
        }
    } else if (CHANNEL_MAX_SET_POINT.equals(channelUID.getId())) {
        if (command instanceof QuantityType) {
            addTemperatureUpdateRequest("target_temperature_high_c", "target_temperature_high_f", (QuantityType<Temperature>) command);
        }
    } else if (CHANNEL_MIN_SET_POINT.equals(channelUID.getId())) {
        if (command instanceof QuantityType) {
            addTemperatureUpdateRequest("target_temperature_low_c", "target_temperature_low_f", (QuantityType<Temperature>) command);
        }
    } else if (CHANNEL_MODE.equals(channelUID.getId())) {
        if (command instanceof StringType) {
            addUpdateRequest("hvac_mode", Mode.valueOf(((StringType) command).toString()));
        }
    } else if (CHANNEL_SET_POINT.equals(channelUID.getId())) {
        if (command instanceof QuantityType) {
            addTemperatureUpdateRequest("target_temperature_c", "target_temperature_f", (QuantityType<Temperature>) command);
        }
    }
}

void sendMessage(Object nhcMessage){
    PrintWriter writer = this.nhcOut;
    String json = gsonOut.toJson(nhcMessage);
    logger.debug("Niko Home Control: send json {} from thread {}", json, Thread.currentThread().getId());
    if (writer != null) {
        writer.println(json);
    }
    if ((writer == null) || (writer.checkError())) {
        logger.warn("Niko Home Control: error sending message, trying to restart communication");
        restartCommunication();
        logger.debug("Niko Home Control: resend json {} from thread {}", json, Thread.currentThread().getId());
        writer = this.nhcOut;
        if (writer != null) {
            writer.println(json);
        }
        if ((writer == null) || (writer.checkError())) {
            logger.warn("Niko Home Control: error resending message");
        }
    }
}

