String decrypt(byte[] data,int length,String decrypt,InputStream inputStream,int length){
    StringBuilder sb = new StringBuilder();
    int in;
    int key = KEY;
    while (sb.length() < length && (in = inputStream.read()) != -1) {
        int nextKey = in;
        sb.append((char) (in ^ key));
        key = nextKey;
    }
    return sb.toString();
}

