boolean matchCriteria(Set criteria,Set target){
    if ((criteria == null) || (criteria.isEmpty()))
        return true;
    for (Iterator i = target.iterator(); i.hasNext(); ) {
        String t = (String) i.next();
        if (criteria.contains(t))
            return true;
    }
    return false;
}

String[] getColumnClasses(UIData data){
    String values = (String) data.getAttributes().get("columnClasses");
    if (values == null) {
        return (new String[0]);
    }
    values = values.trim();
    ArrayList<String> list = new ArrayList<String>();
    while (values.length() > 0) {
        int comma = values.indexOf(",");
        if (comma >= 0) {
            list.add(values.substring(0, comma).trim());
            values = values.substring(comma + 1);
        } else {
            list.add(values.trim());
            values = "";
        }
    }
    String[] results = new String[list.size()];
    return ((String[]) list.toArray(results));
}

