int getPasswordStrength(String passwd){
    int upper = 0, lower = 0, numbers = 0, special = 0, length = 0, strength = 0;
    Pattern p;
    Matcher m;
    if (StringUtils.isBlank(passwd)) {
        log.debug("Password null");
        return NONE;
    }
    length = passwd.length();
    if (length < 5) {
        strength = (strength + 3);
        log.debug("3 points for length (" + length + ")");
    } else if (length > 4 && passwd.length() < 8) {
        strength = (strength + 6);
        log.debug("6 points for length (" + length + ")");
    } else if (length > 7 && passwd.length() < 16) {
        strength = (strength + 12);
        log.debug("12 points for length (" + length + ")");
    } else if (length > 15) {
        strength = (strength + 18);
        log.debug("18 points for length (" + length + ")");
    }
    p = Pattern.compile(".??[a-z]");
    m = p.matcher(passwd);
    while (m.find()) {
        lower++;
    }
    if (lower > 0) {
        strength = (strength + 1);
        log.debug("1 point for a lower case character");
    }
    p = Pattern.compile(".??[A-Z]");
    m = p.matcher(passwd);
    while (m.find()) {
        upper++;
    }
    if (upper > 0) {
        strength = (strength + 5);
        log.debug("5 points for an upper case character");
    }
    p = Pattern.compile(".??[0-9]");
    m = p.matcher(passwd);
    while (m.find()) {
        numbers += 1;
    }
    if (numbers > 0) {
        strength = (strength + 5);
        log.debug("5 points for a number");
        if (numbers > 1) {
            strength = (strength + 2);
            log.debug("2 points for at least two numbers");
            if (numbers > 2) {
                strength = (strength + 3);
                log.debug("3 points for at least three numbers");
            }
        }
    }
    p = Pattern.compile(".??[:,!,@,#,$,%,^,&,*,?,_,~]");
    m = p.matcher(passwd);
    while (m.find()) {
        special += 1;
    }
    if (special > 0) {
        strength = (strength + 5);
        log.debug("5 points for a special character");
        if (special > 1) {
            strength += (strength + 5);
            log.debug("5 points for at least two special characters");
        }
    }
    if (upper > 0 && lower > 0) {
        strength = (strength + 2);
        log.debug("2 combo points for upper and lower letters");
    }
    if ((upper > 0 || lower > 0) && numbers > 0) {
        strength = (strength + 2);
        log.debug("2 combo points for letters and numbers");
    }
    if ((upper > 0 || lower > 0) && numbers > 0 && special > 0) {
        strength = (strength + 2);
        log.debug("2 combo points for letters, numbers and special chars");
    }
    if (upper > 0 && lower > 0 && numbers > 0 && special > 0) {
        strength = (strength + 2);
        log.debug("2 combo points for upper and lower case letters, numbers and special chars");
    }
    if (strength < 16) {
        log.debug("very weak");
        return VERY_WEAK;
    } else if (strength > 15 && strength < 25) {
        log.debug("weak");
        return WEAK;
    } else if (strength > 24 && strength < 35) {
        log.debug("mediocre");
        return MEDIOCRE;
    } else if (strength > 34 && strength < 45) {
        log.debug("strong");
        return STRONG;
    } else {
        log.debug("very strong");
        return VERY_STRONG;
    }
}

void saveNodeAndMetaData(HierarchyPersistentNode pNode,HierarchyNodeMetaData metaData){
    Set<HierarchyPersistentNode> pNodes = new HashSet<HierarchyPersistentNode>();
    pNodes.add(pNode);
    Set<HierarchyNodeMetaData> metaDatas = new HashSet<HierarchyNodeMetaData>();
    metaDatas.add(metaData);
    Set[] entitySets = new Set[] { pNodes, metaDatas };
    dao.saveMixedSet(entitySets);
    if (metaData.getId() == null) {
        if (pNode.getId() != null) {
            dao.delete(pNode);
        }
        throw new RuntimeException("Metadata didn't save, node was removed: " + pNode);
    } else if (pNode.getId() == null) {
        if (metaData.getId() != null) {
            dao.delete(metaData);
        }
        throw new RuntimeException("Metadata didn't save, metaData was removed: " + metaData);
    } else if (!metaData.getId().equals(pNode.getId())) {
        int i = 0;
        if (pNode.getId() > metaData.getId()) {
            while (i < 100 && metaData.getId() != null && pNode.getId() != metaData.getId()) {
                dao.delete(metaData);
                metaData.setId(null);
                dao.save(metaData);
                i++;
            }
        } else {
            while (i < 100 && pNode.getId() != null && pNode.getId() != metaData.getId()) {
                dao.delete(pNode);
                pNode.setId(null);
                dao.save(pNode);
                i++;
            }
        }
        if (pNode.getId() == null || metaData.getId() == null || pNode.getId() != metaData.getId()) {
            throw new RuntimeException("Node ID: " + pNode.getId() + " doesn't match Metadata ID: " + metaData.getId());
        }
    }
}

