void removeBytes(int pos,int len){
    if (!(between(pos, 0, (size - 1)) && between((pos + len), 0, size))) {
        throw new IndexOutOfBoundsException();
    }
    BlobNode startNode, endNode;
    int startPos, endPos;
    startPos = seek(pos);
    startNode = curr;
    endPos = seek(pos + len);
    endNode = curr;
    if (startNode == endNode) {
        System.arraycopy(curr.data, endPos, curr.data, startPos, (curr.size - endPos));
        curr.size -= len;
    } else {
        BlobNode newNode = new BlobNode(Math.max(nodeSize, (startPos + (endNode.size - endPos))));
        System.arraycopy(startNode.data, 0, newNode.data, 0, startPos);
        System.arraycopy(endNode.data, endPos, newNode.data, startPos, (endNode.size - endPos));
        newNode.size = (startPos + 1) + (endNode.size - endPos);
        newNode.next = endNode.next;
        BlobNode before = findBefore(startNode);
        if (before == null) {
            head = newNode;
        } else {
            before.next = newNode;
        }
    }
    size -= len;
}

