short shortAt(int pos){
    if (!between(pos, 0, (size - 2))) {
        throw new IndexOutOfBoundsException();
    }
    short ret = 0x0, temp;
    beginEnumeration(pos);
    for (int x = 0; x < 2; x++) {
        temp = (short) ((nextByte() << (1 * 8)) >>> (1 * 8));
        ret |= (temp << (8 * (1 - x)));
    }
    return ret;
}

