PermissionLevel createDefaultPermissionLevel(String name,String typeUuid,PermissionsMask mask){
    if (log.isDebugEnabled()) {
        log.debug("createDefaultPermissionLevel executing(" + name + "," + typeUuid + "," + mask + ")");
    }
    if (name == null || typeUuid == null || mask == null) {
        throw new IllegalArgumentException("Null Argument");
    }
    PermissionLevel newPermissionLevel = new PermissionLevelImpl();
    Date now = new Date();
    newPermissionLevel.setName(name);
    newPermissionLevel.setUuid(idManager.createUuid());
    newPermissionLevel.setCreated(now);
    newPermissionLevel.setCreatedBy("admin");
    newPermissionLevel.setModified(now);
    newPermissionLevel.setModifiedBy("admin");
    newPermissionLevel.setTypeUuid(typeUuid);
    for (Iterator<Entry<String, Boolean>> i = mask.entrySet().iterator(); i.hasNext(); ) {
        Entry<String, Boolean> entry = i.next();
        String key = entry.getKey();
        Boolean value = entry.getValue();
        try {
            PropertyUtils.setSimpleProperty(newPermissionLevel, key, value);
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }
    return newPermissionLevel;
}

FileSystemHandler getFileSystemHandler(Properties p,String fileSystemHandlerName){
    String clazz = p.getProperty(fileSystemHandlerName);
    log.info("Building FileSystemHandler: " + clazz);
    Class<? extends FileSystemHandler> fshClass = Class.forName(clazz).asSubclass(FileSystemHandler.class);
    FileSystemHandler fsh = fshClass.newInstance();
    Enumeration<String> propertyNames = (Enumeration<String>) p.propertyNames();
    while (propertyNames.hasMoreElements()) {
        String fullProperty = propertyNames.nextElement();
        if (fullProperty.startsWith(fileSystemHandlerName + ".")) {
            String property = fullProperty.substring(fullProperty.indexOf(".") + 1);
            log.info("Setting property: " + property);
            BeanUtils.setProperty(fsh, property, p.getProperty(fullProperty));
        }
    }
    try {
        log.info("Check if there is a init method...");
        MethodUtils.invokeExactMethod(fsh, "init", (Object[]) null);
        log.info("init method invoked...");
    } catch (NoSuchMethodException e) {
        log.info("No init method...");
    }
    log.info("Done with FileSystemHandler: " + clazz);
    return fsh;
}

