int indexOf(byte b,int indexOf,byte b,int pos){
    if (!between(pos, 0, (size - 1))) {
        throw new IndexOutOfBoundsException();
    }
    beginEnumeration(pos);
    int currentPos = pos;
    while (hasMoreBytes()) {
        if (nextByte() == b) {
            return currentPos;
        }
        currentPos++;
    }
    return -1;
}

