Long makeLastModified(Object lm){
    Long lastModified = null;
    if (lm != null) {
        Class<?> c = lm.getClass();
        if (Date.class.isAssignableFrom(c)) {
            lastModified = ((Date) lm).getTime();
        } else if (Long.class.isAssignableFrom(c)) {
            lastModified = ((Long) lm);
        } else if (String.class.isAssignableFrom(c)) {
            try {
                lastModified = new Long((String) lm);
            } catch (NumberFormatException e) {
            }
        } else {
            log.warn("EntityRequestHandler: Unknown type returned for 'lastModified' (not Date, Long, String): " + lm.getClass() + ", using the default value of current time instead");
        }
    }
    return lastModified;
}

IOException copyRange(InputStream istream,OutputStream ostream,long start,long end){
    try {
        istream.skip(start);
    } catch (IOException e) {
        return e;
    }
    IOException exception = null;
    long bytesToRead = end - start + 1;
    byte[] buffer = new byte[STREAM_BUFFER_SIZE];
    int len = buffer.length;
    while ((bytesToRead > 0) && (len >= buffer.length)) {
        try {
            len = istream.read(buffer);
            if (bytesToRead >= len) {
                ostream.write(buffer, 0, len);
                bytesToRead -= len;
            } else {
                ostream.write(buffer, 0, (int) bytesToRead);
                bytesToRead = 0;
            }
        } catch (IOException e) {
            exception = e;
            len = -1;
        }
        if (len < buffer.length)
            break;
    }
    return exception;
}

