                if (message != null && message.getCreatedBy().equals(userId) && !message.getDraft() && ((message.getApproved() != null && message.getApproved()) || !topic.getModerated()) && !message.getDeleted()) {
                    needToPost = false;
                    break;
                }
            }
            if (needToPost && !(uiPermissionsManager.isChangeSettings(topic, (DiscussionForum) topic.getBaseForum(), userId) || uiPermissionsManager.isPostToGradebook(topic, (DiscussionForum) topic.getBaseForum(), userId) || uiPermissionsManager.isModeratePostings(topic, (DiscussionForum) topic.getBaseForum(), userId))) {
                returnList.add(userId);
            }
        }
    }
    return returnList;
}

List filterNoAccessMessages(List currentList,List removeList){
    final List resultList = new ArrayList();
    if (currentList.isEmpty() || removeList.isEmpty()) {
        return currentList;
    }
    final Iterator currentIter = currentList.iterator();
    while (currentIter.hasNext()) {
        final Object[] resultValues = new Object[3];
        Object[] removeValues;
        Object[] currentValues = null;
        if (currentIter.hasNext()) {
            currentValues = (Object[]) currentIter.next();
        }
        if (currentValues == null)
            throw new IllegalStateException("currentValues == null");
        final int pos = indexOf((String) currentValues[0], getSiteIds(removeList));
        if (pos != -1) {
            removeValues = (Object[]) removeList.get(pos);
            if (((String) currentValues[1]).equals(removeValues[1])) {
                resultValues[0] = currentValues[0];
                resultValues[2] = Integer.valueOf(((Integer) currentValues[2]).intValue() - ((Integer) removeValues[2]).intValue());
            }
            resultList.add(resultValues);
            removeList.remove(pos);
        } else {
            resultList.add(currentValues);
        }
    }
    return resultList;
}

