long longAt(int pos){
    if (!between(pos, 0, (size - 8))) {
        throw new IndexOutOfBoundsException();
    }
    long ret = 0x0, temp;
    beginEnumeration(pos);
    for (int x = 0; x < 8; x++) {
        temp = ((long) nextByte() << (7 * 8)) >>> (7 * 8);
        ret |= (temp << (8 * (7 - x)));
    }
    return ret;
}

