String convertLockIfDropbox(String lock,String id){
    if (id == null) {
        return null;
    }
    if (id.startsWith(COLLECTION_DROPBOX)) {
        String[] parts = StringUtil.split(id, "/");
        if (parts.length >= 3) {
            boolean authDropboxGroupsCheck = true;
            String ref = getReference(id);
            if (parts.length >= 4) {
                String userId = parts[3];
                if ((userId == null) || isDropboxMaintainer(parts[2]) || (!isDropboxOwnerInCurrentUserGroups(ref, userId))) {
                    authDropboxGroupsCheck = false;
                }
            }
            if (m_securityService.unlock(AUTH_DROPBOX_GROUPS, ref)) {
                if (authDropboxGroupsCheck) {
                    return AUTH_DROPBOX_GROUPS;
                } else {
                    return AUTH_DROPBOX_MAINTAIN;
                }
            } else {
                return AUTH_DROPBOX_MAINTAIN;
            }
        }
    }
    return lock;
}

void printContents(){
    int x, bytesPast;
    String holdStr;
    char[] rep = new char[16];
    byte b;
    long cksum = checksum();
    int currWidth = 86;
    holdStr = ("Blob: length = " + size + " -- Checksum = " + toHex(cksum) + " ");
    log.debug("{}{}", holdStr, strstr((currWidth - holdStr.length()), '-'));
    if (size == 0) {
        log.debug(strstr(currWidth, '-'));
        return;
    }
    log.debug("     0 | ");
    bytesPast = 0;
    beginEnumeration(0);
    while (hasMoreBytes()) {
        b = nextByte();
        log.debug(toHex(b));
        if (between(b, 32, 126)) {
            rep[bytesPast % 16] = (char) b;
        } else {
            rep[bytesPast % 16] = '.';
        }
        bytesPast++;
        if ((bytesPast % 16) == 0) {
            log.debug("   <");
            for (x = 0; x < 16; x++) {
                log.debug("{}", rep[x]);
            }
            log.debug(">");
            if (hasMoreBytes()) {
                holdStr = Integer.toString(bytesPast);
                log.debug(spaces(6 - holdStr.length()));
                log.debug(holdStr);
                log.debug(" | ");
            }
        } else if ((bytesPast % 4) == 0) {
            log.debug("   ");
        } else {
            log.debug(" ");
        }
    }
    if ((bytesPast % 16) != 0) {
        for (x = (bytesPast % 16); x < 16; x++) {
            log.debug("  ");
            rep[x] = ' ';
            if ((x % 4) == 0) {
                log.debug("   ");
            } else {
                log.debug(" ");
            }
        }
        log.debug("   <");
        for (x = 0; x < 16; x++) {
            log.debug("{}", rep[x]);
        }
        log.debug(">");
    }
    holdStr = ("---------------" + strstr(String.valueOf(size).length(), '-') + "--- Checksum = " + toHex(cksum) + " ");
    log.debug(holdStr);
    log.debug(strstr((currWidth - holdStr.length()), '-'));
}

