            }
            int keyLoc = overallData.indexOf(key);
            if (keyLoc > -1) {
                sb.append(overallData.subSequence(curLoc, keyLoc));
                sb.append(value);
                curLoc = keyLoc + key.length();
            }
        }
    }
    sb.append(overallData.subSequence(curLoc, overallData.length()));
    return sb.toString();
}

