BlobNode findBefore(BlobNode target){
    BlobNode bn;
    if (target == head) {
        return null;
    }
    bn = head;
    while (bn.next != target) {
        if (bn.next == null) {
            throw new NoSuchElementException("Couldn't find BlobNode");
        }
        bn = bn.next;
    }
    return bn;
}

