List populatePodcastArray(String siteId){
    List podEntries = null;
    List entries = new ArrayList();
    try {
        enablePodfeedSecurityAdvisor();
        if (podcastService.isPodcastFolderHidden(siteId)) {
            return entries;
        } else {
            podEntries = podcastService.getPodcasts(siteId);
            securityService.popAdvisor();
            podEntries = podcastService.filterPodcasts(podEntries, siteId);
        }
    } catch (PermissionException e) {
        log.error("PermissionException getting podcasts in order to generate podfeed for site: " + siteId + ". " + e.getMessage(), e);
        throw new PodcastException(e);
    } catch (Exception e) {
        log.info(e.getMessage() + "for site: " + siteId, e);
        throw new PodcastException(e);
    } finally {
        securityService.popAdvisor();
    }
    if (podEntries != null) {
        Iterator podcastIter = podEntries.iterator();
        while (podcastIter.hasNext()) {
            ContentResource podcastResource = (ContentResource) podcastIter.next();
            ResourceProperties podcastProperties = podcastResource.getProperties();
            Date publishDate = null;
            Date compareDate = null;
            try {
                if (podcastResource.getReleaseDate() != null) {
                    compareDate = new Date(podcastResource.getReleaseDate().getTime());
                    publishDate = podcastService.getGMTdate(podcastResource.getReleaseDate().getTime());
                } else {
                    compareDate = new Date(podcastProperties.getTimeProperty(PodcastService.DISPLAY_DATE).getTime());
                    publishDate = podcastService.getGMTdate(podcastProperties.getTimeProperty(PodcastService.DISPLAY_DATE).getTime());
                }
            } catch (Exception e) {
                log.warn(e.getMessage() + " generating podfeed getting DISPLAY_DATE for entry for site: " + siteId + "while building feed. SKIPPING... " + e.getMessage(), e);
            }
            if (publishDate != null && !hiddenInUI(podcastResource, compareDate)) {
                try {
                    Map podcastMap = new HashMap();
                    podcastMap.put("date", publishDate);
                    podcastMap.put("title", podcastProperties.getPropertyFormatted(ResourceProperties.PROP_DISPLAY_NAME));
                    enablePodfeedSecurityAdvisor();
                    String fileUrl = podcastService.getPodcastFileURL(podcastResource.getId());
                    podcastMap.put("guid", fileUrl);
                    final String podcastFolderId = podcastService.retrievePodcastFolderId(siteId);
                    securityService.popAdvisor();
                    if (!podcastService.isPublic(podcastFolderId)) {
                        fileUrl = convertToDavUrl(fileUrl);
                    }
                    podcastMap.put("url", fileUrl);
                    podcastMap.put("description", podcastProperties.getPropertyFormatted(ResourceProperties.PROP_DESCRIPTION));
                    podcastMap.put("author", podcastProperties.getPropertyFormatted(ResourceProperties.PROP_CREATOR));
                    podcastMap.put("len", Long.parseLong(podcastProperties.getProperty(ResourceProperties.PROP_CONTENT_LENGTH)));
                    podcastMap.put("type", podcastProperties.getProperty(ResourceProperties.PROP_CONTENT_TYPE));
                    entries.add(addPodcast(podcastMap));
                } catch (PermissionException e) {
                    log.error("PermissionException generating podfeed while adding entry for site: " + siteId + ". SKIPPING... " + e.getMessage(), e);
                } catch (IdUnusedException e) {
                    log.warn("IdUnusedException generating podfeed while adding entry for site: " + siteId + ".  SKIPPING... " + e.getMessage(), e);
                }
            }
        }
    }
    securityService.popAdvisor();
    return entries;
}

void loadToolOrder(InputStream in){
    Document doc = Xml.readDocumentFromStream(in);
    Element root = doc.getDocumentElement();
    if (!root.getTagName().equals("toolOrder")) {
        log.info("loadToolOrder: invalid root element (expecting \"toolOrder\"): " + root.getTagName());
        return;
    }
    NodeList rootNodes = root.getChildNodes();
    final int rootNodesLength = rootNodes.getLength();
    for (int i = 0; i < rootNodesLength; i++) {
        Node rootNode = rootNodes.item(i);
        if (rootNode.getNodeType() != Node.ELEMENT_NODE)
            continue;
        Element rootElement = (Element) rootNode;
        if (rootElement.getTagName().equals("category")) {
            String name = StringUtils.trimToNull(rootElement.getAttribute("name"));
            if (name != null) {
                List<String> order = m_toolOrders.get(name);
                if (order == null) {
                    order = new Vector();
                    m_toolOrders.put(name, order);
                    List<String> required = new Vector();
                    m_toolsRequired.put(name, required);
                    List<String> defaultTools = new Vector();
                    m_defaultTools.put(name, defaultTools);
                    List<String> toolCategories = new Vector();
                    m_toolCategoriesList.put(name, toolCategories);
                    Map<String, List<String>> toolCategoryMappings = new HashMap();
                    m_toolCategoriesMap.put(name, toolCategoryMappings);
                    Map<String, String> toolToCategoryMap = new HashMap();
                    m_toolToToolCategoriesMap.put(name, toolToCategoryMap);
                    NodeList nodes = rootElement.getChildNodes();
                    final int nodesLength = nodes.getLength();
                    for (int c = 0; c < nodesLength; c++) {
                        Node node = nodes.item(c);
                        if (node.getNodeType() != Node.ELEMENT_NODE)
                            continue;
                        Element element = (Element) node;
                        if (element.getTagName().equals("tool")) {
                            processTool(element, order, required, defaultTools);
                        } else if (element.getTagName().equals("toolCategory")) {
                            processCategory(element, order, required, defaultTools, toolCategories, toolCategoryMappings, toolToCategoryMap);
                        }
                    }
                }
            }
        }
    }
}

EntityReference parseReference(String reference){
    String prefix = EntityReference.getPrefix(reference);
    EntityReference ref = null;
    if (entityProviderManager.getProviderByPrefix(prefix) != null) {
        ReferenceParseable provider = entityProviderManager.getProviderByPrefixAndCapability(prefix, ReferenceParseable.class);
        if (provider == null) {
            ref = new EntityReference(reference);
        } else {
            EntityReference exemplar = provider.getParsedExemplar();
            if (exemplar == null) {
                ref = new EntityReference(reference);
            } else {
                if (exemplar.getClass() == EntityReference.class) {
                    ref = new EntityReference(reference);
                } else {
                    try {
                        Constructor<? extends Object> m = exemplar.getClass().getConstructor(String.class);
                        ref = (EntityReference) m.newInstance(reference);
                    } catch (SecurityException e) {
                        throw new RuntimeException("Failed to invoke a constructor which takes a single string " + "(reference=" + reference + ") for class: " + exemplar.getClass(), e);
                    } catch (IllegalArgumentException e) {
                        throw new RuntimeException("Failed to invoke a constructor which takes a single string " + "(reference=" + reference + ") for class: " + exemplar.getClass(), e);
                    } catch (NoSuchMethodException e) {
                        throw new RuntimeException("Failed to invoke a constructor which takes a single string " + "(reference=" + reference + ") for class: " + exemplar.getClass(), e);
                    } catch (InstantiationException e) {
                        throw new RuntimeException("Failed to invoke a constructor which takes a single string " + "(reference=" + reference + ") for class: " + exemplar.getClass(), e);
                    } catch (IllegalAccessException e) {
                        throw new RuntimeException("Failed to invoke a constructor which takes a single string " + "(reference=" + reference + ") for class: " + exemplar.getClass(), e);
                    } catch (InvocationTargetException e) {
                        throw new RuntimeException("Failed to invoke a constructor which takes a single string " + "(reference=" + reference + ") for class: " + exemplar.getClass(), e);
                    }
                }
            }
        }
    }
    return ref;
}

String processSetTriggerProperties(){
    Scheduler scheduler = schedulerManager.getScheduler();
    if (scheduler == null) {
        log.error("Scheduler is down!");
        return "error";
    }
    configurableJobErrorMessages = new LinkedList<String>();
    try {
        final JobDetail jd = selectedJobDetailWrapper.getJobDetail();
        final ConfigurableJobBeanWrapper configurableJob = getConfigurableJobBeanWrapper();
        final TriggerWrapper triggerWrapper = getTriggerWrapper();
        final Trigger trigger = triggerWrapper.getTrigger();
        final List<ConfigurablePropertyWrapper> properties = getConfigurableProperties();
        final ResourceBundle jobRb = ResourceBundle.getBundle(configurableJob.getResourceBundleBase());
        final ConfigurableJobPropertyValidator validator = configurableJob.getConfigurableJobPropertyValidator();
        final JobDataMap dataMap = trigger.getJobDataMap();
        for (ConfigurablePropertyWrapper wrapper : properties) {
            final ConfigurableJobProperty property = wrapper.getJobProperty();
            final String label = property.getLabelResourceKey(), value = wrapper.getValue();
            if (property.isRequired() && (value == null || value.trim().length() == 0)) {
                String propName = (jobRb != null) ? jobRb.getString(label) : label, msg = null;
                if (propName == null)
                    propName = label;
                try {
                    msg = rb.getString("properties_required");
                } catch (MissingResourceException mre) {
                    msg = "&lt;Missing resource string: properties_required&gt;";
                }
                configurableJobErrorMessages.add(msg + ": " + propName);
            } else {
                try {
                    validator.assertValid(label, value);
                } catch (ConfigurableJobPropertyValidationException cjpve) {
                    String errorKey = cjpve.getMessage(), errorMessage = jobRb.getString(errorKey);
                    configurableJobErrorMessages.add((errorMessage == null) ? errorKey : errorMessage);
                    continue;
                }
                dataMap.put(property.getLabelResourceKey(), value);
            }
        }
        if (!configurableJobErrorMessages.isEmpty()) {
            return null;
        }
        scheduleTrigger(triggerWrapper);
    } catch (Exception e) {
        log.error("Failed to create job.", e);
        return "error";
    }
    return "edit_triggers";
}

