        log.error("Scheduler is down!");
        return "error";
    }
    configurableJobErrorMessages = new LinkedList<String>();
    try {
        JobDetail jd = getJobDetail();
        JobBeanWrapper job = getSchedulerManager().getJobBeanWrapper(selectedClass);
        final ConfigurableJobBeanWrapper configurableJob = getConfigurableJobBeanWrapper();
        final List<ConfigurablePropertyWrapper> properties = getConfigurableProperties();
        final ResourceBundle jobRb = ResourceBundle.getBundle(configurableJob.getResourceBundleBase());
        final ConfigurableJobPropertyValidator validator = configurableJob.getConfigurableJobPropertyValidator();
        if (jd != null) {
            final JobDataMap dataMap = jd.getJobDataMap();
            for (ConfigurablePropertyWrapper wrapper : properties) {
                final ConfigurableJobProperty property = wrapper.getJobProperty();
                final String label = property.getLabelResourceKey(), value = wrapper.getValue();
                if (property.isRequired() && (value == null || value.trim().length() == 0)) {
                    String propName = (jobRb != null) ? jobRb.getString(label) : label, msg = null;
                    if (propName == null)
                        propName = label;
                    try {
                        msg = rb.getString("properties_required");
                    } catch (MissingResourceException mre) {
                        msg = "&lt;Missing resource string: properties_required&gt;";
                    }
                    configurableJobErrorMessages.add(msg + ": " + propName);
                } else {
                    try {
                        validator.assertValid(label, value);
                    } catch (ConfigurableJobPropertyValidationException cjpve) {
                        String errorKey = cjpve.getMessage(), errorMessage = jobRb.getString(errorKey);
                        configurableJobErrorMessages.add((errorMessage == null) ? errorKey : errorMessage);
                        continue;
                    }
                    dataMap.put(property.getLabelResourceKey(), value);
                }
            }
            if (!configurableJobErrorMessages.isEmpty()) {
                return null;
            }
        } else {
            setJobDetail(createJobDetail(job));
            return null;
        }
        scheduler.addJob(jd, false);
        jobName = null;
        jobDetail = null;
        processRefreshJobs();
        return "jobs";
    } catch (Exception e) {
        log.error("Failed to create job.", e);
        return "error";
    }
}

