String getString(String getString,int pos,int len,String getString,boolean useHighByte,int pos,int len){
    if ((pos == 0) && (len == 0)) {
        return "";
    }
    if (!(between(pos, 0, (size - 1)) && between((pos + (len * (useHighByte ? 2 : 1))), 0, size))) {
        throw new IndexOutOfBoundsException();
    }
    StringBuilder ret = new StringBuilder(len);
    char c;
    beginEnumeration(pos);
    for (int x = 0; x < len; x++) {
        if (useHighByte) {
            c = (char) (nextByte() << 8);
            c |= nextByte();
        } else {
            c = (char) nextByte();
        }
        ret.append(c);
    }
    return ret.toString();
}

