Edit editResource(String id){
    Edit edit = null;
    if (m_locksAreInDb) {
        if ("oracle".equals(m_sql.getVendor())) {
            final List<Entity> l = new ArrayList<Entity>();
            Connection lock = null;
            if (m_user instanceof EntityReaderHandler) {
                String statement = "select XML from " + m_resourceTableName + " where ( " + m_resourceTableIdField + " = '" + StorageUtils.escapeSql(caseId(id)) + "' )" + " for update nowait";
                lock = m_sql.dbReadLock(statement, new SqlReader() {
                    public Object readSqlResultRecord(ResultSet result) {
                        try {
                            l.add(readResource(result.getBytes(1)));
                        } catch (SQLException e) {
                            log.warn("Failed to retrieve record ", e);
                        }
                        return null;
                    }
                });
            } else {
                String statement = "select BENTRY, XML from " + m_resourceTableName + " where ( " + m_resourceTableIdField + " = '" + StorageUtils.escapeSql(caseId(id)) + "' )" + " for update nowait";
                lock = m_sql.dbReadLock(statement, new SqlReader() {
                    public Object readSqlResultRecord(ResultSet result) {
                        try {
                            l.add(readResource(result.getBytes(1)));
                        } catch (SQLException e) {
                            log.warn("Failed to retrieve record ", e);
                        }
                        return null;
                    }
                });
            }
            if ((lock == null) || (l.size() == 0))
                return null;
            Entity entry = l.get(0);
            edit = m_user.newResourceEdit(null, entry);
            m_locks.put(entry.getReference(), lock);
        } else {
            throw new UnsupportedOperationException("Record locking only available when configured with Oracle database");
        }
    } else if (m_locksAreInTable) {
        Entity entry = getResource(id);
        if (entry == null)
            return null;
        String statement = singleStorageSql.getInsertLocks();
        String sessionId = UsageSessionService.getSessionId();
        if (sessionId == null) {
            sessionId = "";
        }
        Object[] fields = new Object[4];
        fields[0] = m_resourceTableName;
        fields[1] = internalRecordId(caseId(id));
        fields[2] = TimeService.newTime();
        fields[3] = sessionId;
        boolean ok = m_sql.dbWriteFailQuiet(null, statement, fields);
        if (!ok) {
            return null;
        }
        edit = m_user.newResourceEdit(null, entry);
    } else {
        Entity entry = getResource(id);
        if (entry == null)
            return null;
        synchronized (m_locks) {
            if (m_locks.containsKey(entry.getReference()))
                return null;
            edit = m_user.newResourceEdit(null, entry);
            m_locks.put(entry.getReference(), edit);
        }
    }
    return edit;
}

