List getContents(){
    boolean folderHidden = false;
    try {
        if (podcastService.isPodcastFolderHidden(podcastService.getSiteId())) {
            if (!podcastPermissionsService.hasPerm(PodcastPermissionsService.HIDDEN_PERMISSIONS, podcastService.retrievePodcastFolderId(podcastService.getSiteId()))) {
                return new ArrayList();
            } else {
                folderHidden = true;
            }
        }
        contents = podcastService.getPodcasts();
        if (!podcastPermissionsService.canUpdateSite(podcastService.getSiteId())) {
            contents = podcastService.filterPodcasts(contents);
        }
        EventTrackingService ets = (EventTrackingService) ComponentManager.get(EventTrackingService.class);
        String podcastFolderId = podcastService.retrievePodcastFolderId(podcastService.getSiteId());
        if (ets != null && podcastFolderId != null) {
            ets.post(ets.newEvent("podcast.read", podcastFolderId, false));
        }
    } catch (PermissionException e) {
        log.warn("PermissionException getting podcasts for display in site " + podcastService.getSiteId() + ". " + e.getMessage(), e);
        setErrorMessage(PERMISSION_ALERT);
    } catch (InUseException e) {
        log.warn("InUseException while getting podcasts for display" + podcastService.getSiteId() + ". " + e.getMessage(), e);
        setErrorMessage(INTERNAL_ERROR_ALERT);
    } catch (IdInvalidException e) {
        log.error("IdInvalidException while getting podcasts for display " + podcastService.getSiteId() + ". " + e.getMessage(), e);
        setErrorMessage(ID_INVALID_ALERT);
    } catch (InconsistentException e) {
        log.error("InconsistentException while getting podcasts for display " + podcastService.getSiteId() + ". " + e.getMessage(), e);
        setErrorMessage(INTERNAL_ERROR_ALERT);
        return null;
    } catch (IdUsedException e) {
        log.warn("IdUsedException while gettting podcasts for display " + podcastService.getSiteId() + ". " + e.getMessage(), e);
        setErrorMessage(ID_UNUSED_ALERT);
    } catch (IdUnusedException e) {
        log.warn("IdUnusedException while determining if Podcasts folder has HIDDEN permission set" + " for site " + podcastService.getSiteId() + ". " + e.getMessage(), e);
    }
    ArrayList decoratedPodcasts = new ArrayList();
    if (contents != null) {
        Iterator podcastIter = contents.iterator();
        while (podcastIter.hasNext()) {
            try {
                final ContentResource podcastResource = (ContentResource) podcastIter.next();
                ResourceProperties podcastProperties = podcastResource.getProperties();
                DecoratedPodcastBean podcastInfo = getAPodcast(podcastResource, folderHidden);
                if (podcastInfo != null)
                    decoratedPodcasts.add(podcastInfo);
            } catch (EntityPropertyNotDefinedException e) {
                log.error("EntityPropertyNotDefinedException while creating DecoratedPodcastBean " + " for site " + podcastService.getSiteId() + ". SKIPPING..." + e.getMessage(), e);
            } catch (EntityPropertyTypeException e) {
                log.error("EntityPropertyTypeException while creating DecoratedPodcastBean " + " for site " + podcastService.getSiteId() + ". SKIPPING..." + e.getMessage(), e);
            }
        }
    }
    return decoratedPodcasts;
}

